INSERT INTO `about` VALUES (1, '<div class=\"mb-4\">\r\n<h2>Klinik Laa Tahzan</h2>\r\n<p>Klinik Laa Tachzan adalah salahsatu Balai Pengobatan yang ada di Indramayu, yang melayani kesehatan Ibu dan Anak, Perawatan Luka Diabetes, Konseling Diabetes, Konseling Kebutuhan Khusus, Pemeriksaan Ibu Hamil dan Persalinan, Khitan, dan Bedah Minor</p>\r\n</div>\r\n<div class=\"mb-4\">\r\n<h2>Visi &amp; Misi</h2>\r\n<p>Memberikan Pelayanan Terpercaya pada Pasien</p>\r\n<p>Amanah, Sehat, Berkualitas</p>\r\n</div>', 'public/files/ZNsy584llyVf2OexuENSWDsh1kDFWpxvxile5Tpl.png', 'dr. Shintya Dewi', 'Spesialis Diabetes Melitus dan Organ Dalam', 'public/files/JD7eXBYWXBmHy1jGTgMO3yfgKS9FcNgFf99swuud.jpg', 'dr. Ghinan Musyaffa', 'Spesialis Diabetes Melitus dan Organ Dalam', 'public/files/R3U2foccCTJl3RSUAPeDechqS2REA2crESaPKSHv.jpg', '<p><strong>Jam Konsultasi Offline</strong></p>\r\n<p><strong>Senin - Jumat</strong></p>\r\n<p>08.00 - 16.00</p>\r\n<p><strong>Sabtu - Minggu</strong></p>\r\n<p>10.00 - 17.00</p>\r\n<p><strong>Jam Konsultasi Online</strong></p>\r\n<p><strong>Senin - Jumat</strong></p>\r\n<p><strong>Sesi 1 </strong>08.00 - 10.00</p>\r\n<p><strong>Sesi 2 </strong>10.00 - 12.00</p>\r\n<p><strong>Sesi 3 </strong>13.00 - 15.00</p>\r\n<p><strong>Sesi</strong> <strong>4</strong> 14.00 - 16.00</p>\r\n<p><strong>Sabtu - Minggu</strong></p>\r\n<p><strong>Sesi 5 </strong>16.00 - 17.00</p>\r\n<p><strong>Sesi 6 </strong>18.30 -20.00</p>');

